-- Create Table
CREATE TABLE trade_history_crypto (
    trade_date DATETIME NOT NULL,    -- Date and time of the trade
    coin VARCHAR(40) NOT NULL,       -- Name or symbol of the coin
    trade_type VARCHAR(50) NOT NULL, -- Type of trade (buy or sell)
    amount DECIMAL(18, 8) NOT NULL,  -- Amount of coin traded
    price DECIMAL(18, 8) NOT NULL,   -- Price per coin
    total DECIMAL(18, 8) NOT NULL,   -- Total trade value
    fee DECIMAL(18, 8) DEFAULT 0     -- Fee for the trade (default is 0)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

